INSERT INTO `dx_forms_js` VALUES (2,9,'Parāda vai paslēpj kritērija lauku atkarībā no operācijas lauka vērtības','var sel = form_object.find(\"[name=operation_id]\");\nif (sel)\n{\n	var cur_val = 0;\n	if (sel.val() > 0)\n	{\n		cur_val = sel.val();\n	}\n	\n	var show_show_hide_rel_fields = function(val)\n	{\n		//alert(val);\n		//criteria\n		if (val == 0 || val == 6 || val == 7 || val == 9)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=criteria]\").hide();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=criteria]\").show();\n		}\n	};\n	\n	var change_event = function(e)\n	{\n		if (e)\n		{\n			show_show_hide_rel_fields($(this).val());\n		}\n		else\n		{\n			show_show_hide_rel_fields(cur_val);\n		}\n	};\n\n	change_event(null);\n	\n	sel.on(\'change\', change_event);\n}    \n\n\n',NULL,NULL,1,'2016-06-14 18:21:42'),(3,8,'Parāda vai paslēpj SQL lauku, ja izvēlēts skata tips Tabula ar SQL','var sel = form_object.find(\"[name=view_type_id]\");\nif (sel)\n{\n	var cur_val = 0;\n	if (sel.val() > 0)\n	{\n		cur_val = sel.val();\n	}\n	\n	var show_show_hide_rel_fields = function(val)\n	{		\n		/* max_lenght */\n		if (val == 9)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=custom_sql]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=custom_sql]\").hide();\n		}\n	};\n	\n	var change_event = function(e)\n	{\n		if (e)\n		{\n			show_show_hide_rel_fields($(this).val());\n		}\n		else\n		{\n			show_show_hide_rel_fields(cur_val);\n		}\n	};\n\n	change_event(null);\n	\n	sel.on(\'change\', change_event);\n}\n\n',NULL,NULL,NULL,NULL),(5,98,'Parāda vai paslēpj darbinieka lauku, to rāda tikai ja izpildes veids ir Darbinieks','var sel_p = form_object.find(\"[dx_fld_name=task_perform_id]\");\nif (sel_p)\n{\n	var cur_val = 0;\n	if (sel_p.val() > 0)\n	{\n		cur_val = sel_p.val();\n	}\n	\n	var show_show_hide_rel_fields = function(val)\n	{\n		/* If system task then hide human related task fields */\n		if (val == 1)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=employee_id]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=employee_id]\").hide();\n		}\n	};\n	\n	var change_event = function(e)\n	{\n		if (e)\n		{\n			show_show_hide_rel_fields($(this).val());\n		}\n		else\n		{\n			show_show_hide_rel_fields(cur_val);\n		}\n	};\n\n	change_event(null);\n	\n	sel_p.on(\'change\', change_event);\n}',NULL,NULL,1,'2016-08-06 11:16:03'),(6,6,'Parāda vai paslēpj URL lauku atkarībā no izvēlētā formas veida','var sel = form_object.find(\"[name=form_type_id]\");\nif (sel)\n{\n	var cur_val = 0;\n	if (sel.val() > 0)\n	{\n		cur_val = sel.val();\n	}\n	\n	var show_show_hide_rel_fields = function(val)\n	{\n		//max_lenght\n		if (val == 2)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=custom_url]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=custom_url]\").hide();\n		}\n	};\n	\n	var change_event = function(e)\n	{\n		if (e)\n		{\n			show_show_hide_rel_fields($(this).val());\n		}\n		else\n		{\n			show_show_hide_rel_fields(cur_val);\n		}\n	};\n\n	change_event(null);\n	\n	sel.on(\'change\', change_event);\n}',NULL,NULL,NULL,NULL),(7,7,'Nosaka saistīto reģistru no iepriekšējās formas un uzstāda kā noklusēto','var field_obj = form_object.find(\"[name=field_id]\");\r\n var field_val = field_obj.val();\r\n\r\nif (field_obj.attr(\'type\') != \'hidden\')\r\n{\r\n  var form_id = form_object.find(\"[name=form_id]\").val();\r\n  var prev_form = get_previous_form_by_list(form_object.attr(\'id\'), form_id);\r\n\r\n  var list_id = $(\'#\' + prev_form).find(\"[name=list_id]\").val();\r\n\r\n  var list_sel = form_object.find(\"[name=list_id]\");\r\n\r\n  list_sel.val(list_id);\r\n  list_sel.attr(\"disabled\",\"disabled\");\r\n\r\n  var field_val = field_obj.val();\r\n  if (field_val == 0)\r\n  {  \r\n  	load_binded_field(list_sel.attr(\'id\'), field_obj.attr(\'id\'), list_sel.attr(\'dx_binded_field_id\'), list_sel.attr(\'dx_binded_rel_field_id\'));\r\n  }\r\n}',NULL,NULL,NULL,NULL),(10,10,'Uzstāda title_index lauka vērtību - apvieno secību un nosaukumu','var tit = form_object.find(\"input[name=title]\");\r\nvar sec = form_object.find(\"input[name=order_index]\");\r\nvar indx = form_object.find(\"input[name=title_index]\");\r\nvar portal = form_object.find(\"select[name=group_id]\");\r\n\r\nif (tit && sec && indx && portal)\r\n{\r\n	\r\n  	var pad = function(num, size) {\r\n    	var s = \"00000\" + num;\r\n    	return s.substr(s.length-size);\r\n	};	\r\n  \r\n  	var update_fld = function() {\r\n		indx.val(portal.find(\"option:selected\").text() + \": [\" + pad(sec.val(),4) + \"] \" + tit.val()); \r\n	};\r\n	\r\n	tit.focusout(function(){\r\n		update_fld();\r\n	});\r\n	\r\n	sec.focusout(function(){\r\n		update_fld();\r\n	});\r\n}',NULL,NULL,1,'2016-08-22 08:43:26'),(11,154,'Parāda vai paslēpj konfigurācijas laukus atkarībā no izvēlētā lauka tipa','var sel = form_object.find(\"[name=field_type_id]\");\nif (sel)\n{\n	var cur_val = 0;\n	\n  	sel.find(\"option[value=\'0\']\").remove();\n	sel.find(\"option[value=\'2\']\").remove();\n	sel.find(\"option[value=\'3\']\").remove();\n	sel.find(\"option[value=\'4\']\").remove();\n	sel.find(\"option[value=\'6\']\").remove();\n	sel.find(\"option[value=\'8\']\").remove();\n	sel.find(\"option[value=\'10\']\").remove();\n	sel.find(\"option[value=\'11\']\").remove();\n	sel.find(\"option[value=\'13\']\").remove();\n	sel.find(\"option[value=\'14\']\").remove();\n	sel.find(\"option[value=\'16\']\").remove();\n	sel.find(\"option[value=\'17\']\").remove();\n  	\n  	if (sel.val() > 0)\n	{\n		cur_val = sel.val();\n	}\n  \n		\n	var show_hide_rel_fields = function(val)\n	{\n\n		//val_varchar\n		if (val == 1)\n		{\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=val_varchar]\").show();\n		}\n		else\n		{\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=val_varchar]\").hide();\n		}\n		\n		//val_script\n		if (val == 15)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_script]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_script]\").hide();\n		}\n		\n		//val_integer\n		if (val == 5)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_integer]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_integer]\").hide();\n		}\n		\n		//val_date\n		if (val == 9)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_date]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_date]\").hide();\n		}\n		\n		//val_file_name\n		if (val == 12)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_file_name]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_file_name]\").hide();\n		}\n		\n		//val_yesno\n		if (val == 7)\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_yesno]\").show();\n		}\n		else\n		{\n                    form_object.find(\".dx-form-field-line[dx_fld_name_form=val_yesno]\").hide();\n		}\n		\n	}\n	\n	var change_event = function(e)\n	{\n		if (e)\n		{\n			show_hide_rel_fields($(this).val());\n		}\n		else\n		{\n			show_hide_rel_fields(cur_val);\n		}\n	}\n\n	change_event(null);\n	\n	sel.on(\'change\', change_event);\n}\n',NULL,NULL,NULL,NULL),(13,49,'Parāda vai paslēpj laukus atkarībā no izvēlētā attēlošanas veida.','var sel = form_object.find(\"[name=content_id]\");\r\nif (sel)\r\n{\r\n	var cur_val = 0;\r\n	if (sel.val() > 0)\r\n	{\r\n		cur_val = sel.val();\r\n	}\r\n	\r\n	var show_show_hide_rel_fields = function(val)\r\n	{		\r\n		if (val == 1) // teksts\r\n		{\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=is_top_article]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=is_static]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=alternate_url]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=article_text]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=author_id]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=video_galery_id]\").show();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=picture_galery_id]\").show();\r\n			\r\n			form_object.find(\"div[dx_attr=tab] ul.nav-tabs li\").show(); // parādam visus tabus\r\n		}\r\n		else\r\n		{\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=is_top_article]\").hide()\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=is_static]\").hide();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=alternate_url]\").hide();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=article_text]\").hide();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=author_id]\").hide();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=video_galery_id]\").hide();\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=picture_galery_id]\").hide();\r\n						\r\n			form_object.find(\"div[dx_attr=tab] ul.nav-tabs li\").first().find(\"a\").trigger( \"click\" ); // iezīmju tabs ir kā pirmais\r\n			setTimeout(function() {\r\n                form_object.find(\"div[dx_attr=tab] ul.nav-tabs li\").not(\".active\").hide();\r\n            }, 200);			\r\n			\r\n		}\r\n\r\n		if (val == 2) // ārējā saite\r\n		{			\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=outer_url]\").show();\r\n		}\r\n		else\r\n		{\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=outer_url]\").hide()\r\n		}\r\n		\r\n		if (val == 3) // lejuplādējama datne\r\n		{			\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=dwon_file_name]\").show();\r\n		}\r\n		else\r\n		{\r\n			form_object.find(\".dx-form-field-line[dx_fld_name_form=dwon_file_name]\").hide()\r\n		}	\r\n		\r\n	}\r\n	\r\n	var change_event = function(e)\r\n	{\r\n		if (e)\r\n		{\r\n			show_show_hide_rel_fields($(this).val());\r\n		}\r\n		else\r\n		{\r\n			show_show_hide_rel_fields(cur_val);\r\n		}\r\n	}\r\n\r\n	change_event(null);\r\n	\r\n	sel.on(\'change\', change_event);\r\n}',1,'2016-03-14 13:41:15',1,'2016-03-14 16:06:36'),(14,49,'Automātiski formatē ziņas virsrakstu kā norādes tekstu - URL formātā.','var tit = form_object.find(\"input[name=title]\");\r\nvar altern = form_object.find(\"input[name=alternate_url]\");\r\nvar sel = form_object.find(\"[name=content_id]\");\r\n\r\nif (tit.length && altern.length && sel.length)\r\n{	\r\n	var slug = function(str) {\r\n	  str = str.replace(/^\\s+|\\s+$/g, \'\'); // trim\r\n	  str = str.toLowerCase();\r\n\r\n	  // remove accents, swap ñ for n, etc\r\n	  var from = \"āčēģīķļņšūž·/_,:;\";\r\n	  var to   = \"acegiklnsuz------\";\r\n	  for (var i=0, l=from.length ; i<l ; i++) {\r\n		str = str.replace(new RegExp(from.charAt(i), \'g\'), to.charAt(i));\r\n	  }\r\n\r\n	  str = str.replace(/[^a-z0-9 -]/g, \'\') // remove invalid chars\r\n		.replace(/\\s+/g, \'-\') // collapse whitespace and replace by -\r\n		.replace(/-+/g, \'-\'); // collapse dashes\r\n\r\n	  return str;\r\n	};\r\n	\r\n	var change_event = function(e)\r\n	{\r\n		if (e && altern.is(\":visible\"))\r\n		{\r\n			altern.val(slug($(this).val()));\r\n		}\r\n		else\r\n		{\r\n			altern.val(\'\');\r\n		}\r\n	}\r\n	\r\n	var change_type = function(e)\r\n	{\r\n		if (e)\r\n		{\r\n			if (sel.val() != 1)\r\n			{\r\n				altern.val(\'\');\r\n			}\r\n                        else\r\n                        {\r\n                            altern.val(slug(tit.val()));\r\n                        }\r\n		}\r\n	}\r\n	\r\n	tit.on(\'keyup\', change_event);\r\n	tit.on(\'change\', change_event);\r\n	\r\n	sel.on(\'change\', change_type);\r\n}',1,'2016-03-14 17:09:08',1,'2016-03-14 17:40:44'),(15,198,'Pievieno paskaidrojumu un bridinājumu bloku izveidošanas pogas TINYMCE redaktoram','tinymce.PluginManager.add(\'custom_buttons\', function(editor, url) {\r\n    // Inicializē CSS priekš redaktora satura\r\n    editor.on(\'PreInit\', function(e) {\r\n        tinymce.activeEditor.dom.loadCSS(DX_CORE.site_public_url + \'css/pages/book_chapter.css\');\r\n        tinymce.activeEditor.dom.loadCSS(DX_CORE.site_public_url + \'css/pages/book_chapter_editor.css\');\r\n    });\r\n\r\n    // Pārveido tabulu par galeriju pievienojot tai klasi\r\n    // Adds a menu item to the insert menu\r\n    editor.addMenuItem(\'create_gallery\', {\r\n        text: \'Izveidot galeriju\',\r\n        context: \'insert\',\r\n        icon: false,\r\n        onclick: function() {\r\n            $(editor.selection.getNode()).closest(\'table\').addClass(\'dx-book-html-table-gallery\');\r\n        }\r\n    });\r\n\r\n    // Paskaidrojuma bloks\r\n    // Adds a menu item to the insert menu\r\n    editor.addMenuItem(\'insert_comment\', {\r\n        text: \'Paskaidrojuma bloks\',\r\n        context: \'insert\',\r\n        onclick: function() {\r\n            editor.insertContent(\r\n                    \'<table align=\"center\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" class=\"dx-book-html-table-comment\">\' +\r\n                    \'    <tr>\' +\r\n                    \'      <td valign=\"middle\" class=\"dx-book-html-cell-content-wrapper\">\' +\r\n                    \'        <table align=\"center\" border=\"0\" cellspacing=\"0\" cellpadding=\"20\">\' +\r\n                    \'          <tr>\' +\r\n                    \'            <td valign=\"middle\" class=\"dx-book-html-cell-content\"></td>\' +\r\n                    \'          </tr>\' +\r\n                    \'        </table>\' +\r\n                    \'      </td>\' +\r\n                    \'      <td valign=\"top\" align=\"right\">\