INSERT INTO `dx_lists_js` VALUES (1,4029,'Pievienot sarakstam sertifikātu masveida ģenerēšanas pogu','var list_id = list_object.attr(\"dx_list_id\");\nvar open_url = DX_CORE.site_url + \"web/viewer.html?file=\" + DX_CORE.site_url + \"edu/multicert/certs/\" + list_object.attr(\"dx_view_id\") + \"/\" + list_object.attr(\"dx_grid_id\") + \"/\" + moment().format(\"DD_MM_YYYY_HH_mm_ss\") + \".pdf\";\n\nvar btn = $(\'<a class=\"btn btn-white\" target=\"_blank\" href=\"\' + open_url + \'\" style=\"margin-left: 5px;\" type=\"button\">Drukāt</a>\');\nbtn.appendTo(list_object.find(\".dx-grid-butons\"));',NULL,NULL,NULL,NULL),(2,4006,'Pievieno mācību dalībnieku reģistram darbinieku importēšanas pogu','var grid_id = list_object.attr(\'dx_grid_id\');\n\nvar btn = $(\'<button class=\"btn btn-primary\" style=\"margin-left: 3px;\" type=\"button\"><i class=\"fa fa-upload\"></i> Importēt darbiniekus</button>\');\nbtn.appendTo(list_object.find(\".dx-grid-butons\"));\n\nbtn.EduImport();\nbtn.on(\"dx_edu_imported\", function(event) {\n    reload_grid(grid_id);\n});',NULL,NULL,NULL,NULL),(3,4109,'Pievieno sarakstam pogu, ar kuru atvēt grupas komplektēšanas formu','var grid_id = list_object.attr(\'dx_grid_id\');\r\n\r\nvar btn = $(\'<button class=\"btn btn-primary dx-edu-wishlist-popup-open\" style=\"margin-left: 3px;\" type=\"button\">\' +\r\n	\'<i class=\"fa fa-graduation-cap\"></i> \' +\r\n	Lang.get(\'education.wishlist.btn_grid_complect\') +\r\n	\'</button>\');\r\n\r\nbtn.appendTo(list_object.find(\".dx-grid-butons\"));\r\n',NULL,NULL,NULL,NULL),(4,4026,'Iekrāsot pilnās grupas','var seats_offset = list_object.find(\'thead > tr:last > th[fld_name=\"edu_subjects_groups_seats_limit\"]\').prevAll().length;\r\nvar members_offset = list_object.find(\'thead > tr:last > th[fld_name=\"edu_subjects_groups_aggr_memb_count\"]\').prevAll().length;\r\n\r\nlist_object.find(\'tbody > tr\').each(function()\r\n{\r\n	var seats_val = $(this).children(\'td:eq(\' + seats_offset + \')\').text();\r\n	var members_val = $(this).children(\'td:eq(\' + members_offset + \')\').text();\r\n	\r\n	if(parseInt(members_val) >= parseInt(seats_val))\r\n	{\r\n		$(this).css({\r\n			\'background-color\': \'#b5ead7\'\r\n		});\r\n	}\r\n});',NULL,NULL,NULL,NULL);
