INSERT INTO `dx_pages` VALUES (2,'Ieraksta informācija','<div class=\"row\"><!-- Centrālā daļa -->\r\n<div class=\"col-lg-3\">\r\n  [[OBJ=HTML|CODE=DEMO_SCHEDULE]]\r\n  [[OBJ=HTML|CODE=DEMO_CALENDAR]]\r\n</div>\r\n<div class=\"col-lg-6\">\r\n[[OBJ=ITEM]]\r\n</div>\r\n<!-- Labās puses josla -->\r\n  <div class=\"col-lg-3\">\r\n    [[OBJ=HTML|CODE=LOGO]]\r\n    [[OBJ=TOPARTICLES|SOURCE=17|ARTICLEPAGE=2]]\r\n    [[OBJ=HTML|CODE=IT_LINK_AUTH]]  \r\n    [[OBJ=WRITEQ]] \r\n    [[OBJ=HTML|CODE=FAQ_LINK]]\r\n  </div>\r\n</div>',1,'ieraksts',0,NULL,NULL,NULL,'rgba(233,237,239,0.9)',NULL,NULL,1,'2017-07-24 15:54:14',1,NULL),(11,'Attēlu un video galerijas','<div class=\"row\">\r\n  <!-- Centrālā daļa -->\r\n  <div class=\"col-lg-12\">\r\n    <h1>Galerijas</h1>\r\n    [[OBJ=GALERIES|SOURCE=0|ARTICLEPAGE=2]]\r\n  </div>\r\n</div>',1,'galerijas',0,NULL,NULL,NULL,'rgba(233,237,239,0.9)',NULL,NULL,1,'2016-07-10 19:03:55',1,NULL),(14,'Skaidrojumi un ieteikumi','<div class=\"row\"><!-- Centrālā daļa -->\r\n<div class=\"col-lg-3\">\r\n  [[OBJ=HTML|CODE=DEMO_SCHEDULE]]\r\n  [[OBJ=HTML|CODE=DEMO_CALENDAR]]\r\n</div>\r\n<div class=\"col-lg-6\">\r\n[[OBJ=FAQ|SOURCE=0|TITLE=Skaidrojumi_un_ieteikumi|IS_COMPACT=1]]\r\n</div>\r\n<!-- Labās puses josla -->\r\n  <div class=\"col-lg-3\">\r\n    [[OBJ=HTML|CODE=LOGO]]\r\n    [[OBJ=TOPARTICLES|SOURCE=17|ARTICLEPAGE=2]]\r\n    [[OBJ=HTML|CODE=IT_LINK_AUTH]]  \r\n    [[OBJ=WRITEQ]] \r\n    [[OBJ=HTML|CODE=FAQ_LINK]]\r\n  </div>\r\n</div>',1,'skaidrojumi_ieteikumi',0,NULL,NULL,NULL,NULL,NULL,NULL,1,'2017-07-24 16:01:59',1,NULL),(16,'Administratīvie līdzekļi','<h1><i class=\'fa fa-cog\'></i> Administratīvie līdzekļi</h1>\r\n<small>Administratīvie līdzekļi pieejami sistēmas administratoram un paredzēti dažādu administratīvu funkciju izpildei.</small>\r\n<hr>\r\n\r\n<h3>Nevajadzīgo datņu dzēšana</h3>\r\n<p>Līdzeklis nodrošina iespēju dzēst sistemā neizmantotās datnes, kuras nav piesaistītas nevienam ierakstam datu bāzē.</p>\r\n<button class=\"btn btn-primary\" id=\"del_unneded\">Dzēst datnes</button>\r\n\r\n<h3>Auditācijas iestatīšana</h3>\r\n<p>Līdzeklis nodrošina iespēju iestatīt ieraksta izmaiņu vēstures uzkrāšanas funkcionalitāti tām datu bāzes tabulām, kurām tā vēl nav uzstādīta.</p>\r\n<button class=\"btn btn-primary\" id=\"audit_gener\">Iestatīt auditāciju</button>\r\n\r\n<h3>Sistēmas tehniskā dokumentācija</h3>\r\n<p>Līdzeklis nodrošina iespēju ģenerēt sistēmas tehniskās dokumentācijas <a target=\"_blank\" href=\"/assets/global/doc/ppa.pdf\">datni PDF formātā</a>.</p>\r\n<button class=\"btn btn-primary\" id=\"ppa_gener\">Ģenerēt dokumentāciju</button>\r\n\r\n\r\n<script>\r\n	var handleDelFiles = function() {	\r\n		$(\"#del_unneded\").click(function(){\r\n			var item_url = \'/structure/form/delete_unneded_files\';\r\n			var item_title = \'Nevajadzīgo datņu dzēšana\';\r\n			\r\n			get_popup_item_by_id(0, item_url, item_title);		\r\n		});\r\n		\r\n	};\r\n\r\n	var handleAuditGener = function() {\r\n		$(\"#audit_gener\").click(function(){\r\n			var item_url = \'/structure/form/generate_audit\';\r\n			var item_title = \'Auditācijas iestatīšana\';\r\n			\r\n			get_popup_item_by_id(0, item_url, item_title);\r\n			\r\n		});\r\n	};\r\n\r\n	var handlePPAGener = function() {\r\n		$(\"#ppa_gener\").click(function(){\r\n			var item_url = \'/structure/form/ppa_pdf\';\r\n			var item_title = \'Tehniskās dokumentācijas ģenerēšana\';\r\n			\r\n			get_popup_item_by_id(0, item_url, item_title);\r\n			\r\n		});\r\n	};\r\n	\r\n	window.onload = function() {\r\n		handleDelFiles();\r\n		handleAuditGener();\r\n      	handlePPAGener();\r\n	};\r\n	\r\n</script>',1,'admin_tools',0,NULL,NULL,NULL,NULL,1,'2016-05-04 14:54:49',1,'2017-07-03 22:54:26',1,NULL),(17,'Sākums','<div class=\"row\"><!-- Centrālā daļa -->\r\n<div class=\"col-lg-3\">\r\n  [[OBJ=GROUPS_SCHEDULE]]\r\n  [[OBJ=GROUPS_CALENDAR|FILTER_PAGE_URL=http://mindwo.demo/calendar_filter]]\r\n</div>\r\n<div class=\"col-lg-6\">\r\n<!-- Ziņas-->\r\n  [[OBJ=TASKSLIST]]\r\n  [[OBJ=EDU